import{_ as t,c as d,o as s,ae as l}from"./chunks/framework.CliRSA9F.js";const f=JSON.parse('{"title":"事件 (Events)","description":"","frontmatter":{},"headers":[],"relativePath":"markdown/dev/events.md","filePath":"markdown/dev/events.md"}'),o={name:"markdown/dev/events.md"};function n(a,e,i,c,r,g){return s(),d("div",null,[...e[0]||(e[0]=[l('<h1 id="事件-events" tabindex="-1">事件 (Events) <a class="header-anchor" href="#事件-events" aria-label="Permalink to &quot;事件 (Events)&quot;">​</a></h1><p><code>adapter-yunhupro</code> 负责接收来自云湖平台的 Webhook 事件。</p><p>本页将详细说明云湖事件与 Koishi 事件之间的对应关系。</p><p>您可以在插件中通过 <code>ctx.on(&#39;EVENT_NAME&#39;, (session) =&gt; { ... })</code> 来监听这些事件。</p><h2 id="消息事件-message-events" tabindex="-1">消息事件 (Message Events) <a class="header-anchor" href="#消息事件-message-events" aria-label="Permalink to &quot;消息事件 (Message Events)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">云湖事件类型</th><th style="text-align:left;">Koishi 事件</th><th style="text-align:left;">触发时机</th><th style="text-align:left;">Session 字段</th></tr></thead><tbody><tr><td style="text-align:left;"><code>message.receive.normal</code></td><td style="text-align:left;"><code>message</code></td><td style="text-align:left;">收到普通消息（私聊或群聊）</td><td style="text-align:left;"><code>session.type = &#39;message&#39;</code></td></tr><tr><td style="text-align:left;"><code>message.receive.instruction</code></td><td style="text-align:left;"><code>message</code></td><td style="text-align:left;">收到指令消息（通常由斜杠指令 <code>/</code> 触发）</td><td style="text-align:left;"><code>session.type = &#39;message&#39;</code></td></tr></tbody></table><h2 id="成员变动事件-guild-member-events" tabindex="-1">成员变动事件 (Guild Member Events) <a class="header-anchor" href="#成员变动事件-guild-member-events" aria-label="Permalink to &quot;成员变动事件 (Guild Member Events)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">云湖事件类型</th><th style="text-align:left;">Koishi 事件</th><th style="text-align:left;">触发时机</th><th style="text-align:left;">Session 字段</th></tr></thead><tbody><tr><td style="text-align:left;"><code>bot.followed</code></td><td style="text-align:left;"><code>friend-added</code></td><td style="text-align:left;">机器人被用户关注（添加好友）</td><td style="text-align:left;"><code>session.userId</code></td></tr><tr><td style="text-align:left;"><code>group.member.joined</code></td><td style="text-align:left;"><code>guild-member-added</code></td><td style="text-align:left;">新成员通过链接或搜索加入群组</td><td style="text-align:left;"><code>session.userId</code>, <code>session.guildId</code>, <code>session.operatorId</code> (通常是自己)</td></tr><tr><td style="text-align:left;"><code>group.member.invited</code></td><td style="text-align:left;"><code>guild-member-added</code></td><td style="text-align:left;">成员被邀请加入群组</td><td style="text-align:left;"><code>session.userId</code>, <code>session.guildId</code>, <code>session.operatorId</code> (邀请者), <code>session.subtype = &#39;invite&#39;</code></td></tr><tr><td style="text-align:left;"><code>group.member.leaved</code></td><td style="text-align:left;"><code>guild-member-removed</code></td><td style="text-align:left;">成员主动退出群组</td><td style="text-align:left;"><code>session.userId</code>, <code>session.guildId</code>, <code>session.operatorId</code> (自己), <code>session.subtype = &#39;leave&#39;</code></td></tr><tr><td style="text-align:left;"><code>group.member.kicked</code></td><td style="text-align:left;"><code>guild-member-removed</code></td><td style="text-align:left;">成员被管理员踢出群组</td><td style="text-align:left;"><code>session.userId</code>, <code>session.guildId</code>, <code>session.operatorId</code> (操作者), <code>session.subtype = &#39;kick&#39;</code></td></tr></tbody></table><h2 id="群组变动事件-guild-events" tabindex="-1">群组变动事件 (Guild Events) <a class="header-anchor" href="#群组变动事件-guild-events" aria-label="Permalink to &quot;群组变动事件 (Guild Events)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">云湖事件类型</th><th style="text-align:left;">Koishi 事件</th><th style="text-align:left;">触发时机</th><th style="text-align:left;">Session 字段</th></tr></thead><tbody><tr><td style="text-align:left;"><code>group.disbanded</code></td><td style="text-align:left;"><code>guild-deleted</code></td><td style="text-align:left;">群组被解散</td><td style="text-align:left;"><code>session.guildId</code>, <code>session.operatorId</code> (操作者)</td></tr></tbody></table><h2 id="交互事件-interaction-events" tabindex="-1">交互事件 (Interaction Events) <a class="header-anchor" href="#交互事件-interaction-events" aria-label="Permalink to &quot;交互事件 (Interaction Events)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">云湖事件类型</th><th style="text-align:left;">Koishi 事件</th><th style="text-align:left;">触发时机</th><th style="text-align:left;">Session 字段</th></tr></thead><tbody><tr><td style="text-align:left;"><code>bot.shortcut.menu</code></td><td style="text-align:left;"><code>interaction/button</code></td><td style="text-align:left;">用户点击了机器人的快捷菜单</td><td style="text-align:left;"><code>session.userId</code>, <code>session.channelId</code>, <code>session.event.button.id</code> (菜单ID)</td></tr><tr><td style="text-align:left;"><code>button.report.inline</code></td><td style="text-align:left;"><code>interaction/button</code></td><td style="text-align:left;">用户点击了消息中的内联按钮</td><td style="text-align:left;"><code>session.userId</code>, <code>session.channelId</code>, <code>session.messageId</code>, <code>session.event.button.id</code> (按钮ID)</td></tr></tbody></table>',12)])])}const y=t(o,[["render",n]]);export{f as __pageData,y as default};
